def sum_min_distances(galaxies, rows, columns, d=2):
    total_distance = 0
    for galaxy in galaxies:
        for other_galaxy in galaxies:
            ys = sorted([galaxy[0], other_galaxy[0]])
            xs = sorted([galaxy[1], other_galaxy[1]])
            dy = ys[1] - ys[0]
            dx = xs[1] - xs[0]
            for row in rows:
                if row > ys[0] and row < ys[1]:
                    dy += d - 1
            for column in columns:
                if column > xs[0] and column < xs[1]:
                    dx += d - 1
            distance = dy + dx
            total_distance += distance
    return total_distance//2

def solve(puzzle_input):
    grid = [[x for x in y] for y in puzzle_input.strip().splitlines()]
    columns = list(range(0, len(grid[0])))
    rows = []
    galaxies = []
    for y in range(0, len(grid)):
        empty = True
        for x in range(0, len(grid[y])):
            if grid[y][x] == '#':
                empty = False
                if x in columns:
                    columns.remove(x)
                galaxies.append((y, x))
        if empty:
            rows.append(y)

    print(sum_min_distances(galaxies, rows, columns))
    print(sum_min_distances(galaxies, rows, columns, d=1000000))

    return

